        public static HanZi GetHanZi(char ch)
        {
            int pos = _HanZiList.BinarySearch(new HanZi(ch));
            if (pos >= 0) return _HanZiList[pos];
            else return null;
        }
        public class HanZi : IComparable
        {
            public char Zi;
            public string PinYin;
            public int ShengDiao;

            internal HanZi(char ch, string pinyin, int shengdiao)
            {
                this.Zi = ch;
                this.PinYin = pinyin;
                this.ShengDiao = shengdiao;
            }
            internal HanZi(char ch)
            {
                this.Zi = ch;
                this.PinYin = null;
                this.ShengDiao = 0;
            }

            public string FirstPinYin
            {
                get { return (PinYin != null) ? PinYin.Substring(0, 1) : null; }
            }

            public int CompareTo(object obj)
            {
                HanZi hzi = obj as HanZi;
                if (hzi == null) throw new InvalidCastException();

                if (hzi.ShengDiao == 0) return Zi.CompareTo(hzi.Zi);
                if (Zi < hzi.Zi) return -1;
                if (Zi > hzi.Zi) return 1;
                return ShengDiao.CompareTo(hzi.ShengDiao);
            }

        }
